'use strict';

var Accountant = function(config){
  this.investments = 0;
  this.profits = 0;
  this.investmentsElement = config.investmentsElement;
  this.profitsElement = config.profitsElement;
  this.totalElement = config.totalElement;
};

Accountant.prototype.invest = function(number){
  this.investments += number;
  this.render();
};

Accountant.prototype.profit = function(number){
  this.profits += number;
  this.render();
};

Accountant.prototype.total = function(){
  return this.profits - this.investments;
};

Accountant.prototype.render = function(){
  this.investmentsElement.textContent = this.investments;
  this.profitsElement.textContent = this.profits;
  this.totalElement.textContent = this.total();
};

Accountant.prototype.balance = function(){
  console.log('investments is ' + this.investments);
  console.log('profits is ' + this.profits);
};


module.exports = Accountant;
