'use strict';

var Accountant = function(){
  this.investments = 0;
  this.profits = 0;
};

Accountant.prototype.invest = function(number){
  this.investments += number;
};

Accountant.prototype.profit = function(number){
  this.profits += number;
};

Accountant.prototype.balance = function(){
  console.log('investments is ' + this.investments);
  console.log('profits is ' + this.profits);
};

module.exports = Accountant;


