'use strict';

/**
 * Configurations; image paths, DOM elements used for hooks
 *
 * @module config
 */

var select = function(selector){
  return document.querySelector(selector);
};

var config = {
  treasureList: select('#treasure-list'),
  treasurePath: '/images/treasure/',
  consumableList: select('#consumable-list'),
  consumablePath: '/images/consumable/',
  investmentsElement: select('#invest .accountant-figure'),
  profitsElement: select('#profit .accountant-figure'),
  totalElement: select('#total .accountant-figure'),
  areaSelectElement: select('.level-area'),
  levelSelectElement: select('.level-level')
};

module.exports = config;
