'use strict';

var Item = require('item');

var Consumable = function(specs, accountant, config){
  this.imageUrl = config.consumablePath + specs.imageUrl;
  this.listHolder = config.consumableList;

  Item.call(this, specs, accountant);

  this.imgElement.addEventListener('click', this.invest.bind(this));
};

Consumable.prototype = Object.create(Item.prototype);
Consumable.prototype.constructor = Consumable;

Consumable.prototype.invest = function(){
  this.accountant.invest(this.initialPrice);
  this.select();
  console.log('accountant.invest ' + this.initialPrice);
  this.accountant.balance();
};

module.exports = Consumable;
