'use strict';

var Item = function(specs, accountant){
  this.name = specs.name;
  this.initialPrice = specs.initialPrice;

  this.accountant = accountant;

  this.imgElement = document.createElement('img');
  this.imgElement.setAttribute('src', this.imageUrl);
  this.imgElement.setAttribute('alt', this.name);
  this.imgElement.addEventListener('click', this.add.bind(this));

  this.liElement = document.createElement('li');
  this.liElement.classList.add('item');
  this.liElement.appendChild(this.imgElement);
};

Item.prototype.render = function(){
  this.listHolder.appendChild(this.liElement);
  console.log(this.name);
};

Item.prototype.select = function(){
  console.log(this.liElement);
  this.liElement.classList.add('item-selected');
};

Item.prototype.add = function(){
  this.accountant.add(this.initialPrice);
  this.select();
  console.log('Accountant has been given ' + this.initialPrice);
  this.accountant.balance();
};

module.exports = Item;
