'use strict';

var Item = require('item');

var Treasure = function(specs, accountant, config){
  this.imageUrl = config.treasurePath + specs.imageUrl;
  this.listHolder = config.treasureList;

  Item.call(this, specs, accountant);

  this.imgElement.addEventListener('click', this.profit.bind(this));
};

Treasure.prototype = Object.create(Item.prototype);
Treasure.prototype.constructor = Treasure;

Treasure.prototype.profit = function(){
  this.accountant.profit(this.initialPrice);
  this.select();
  console.log('accountant.profit ' + this.initialPrice);
  this.accountant.balance();
};

module.exports = Treasure;
